import React, { useState } from "react";

const App = () => {
  const [billAmount, setBillAmount] = useState("");
  const [tipPercentage, setTipPercentage] = useState(null);
  const [customTipAmount, setCustomTipAmount] = useState("");
  const [numberOfPeople, setNumberOfPeople] = useState("");
  const [tipAmountPerPerson, setTipAmountPerPerson] = useState(0);
  const [totalAmountPerPerson, setTotalAmountPerPerson] = useState(0);

  const handleCalculateTip = () => {
    let tipAmount = 0;

    if (tipPercentage !== null) {
      tipAmount = (billAmount * tipPercentage) / 100;
    } else if (customTipAmount !== "") {
      tipAmount = parseFloat(customTipAmount);
    }

    const totalAmount = parseFloat(billAmount) + tipAmount;
    const numberOfPeopleFloat = parseFloat(numberOfPeople);

    setTipAmountPerPerson(tipAmount / numberOfPeopleFloat);
    setTotalAmountPerPerson(totalAmount / numberOfPeopleFloat);
  };

  return (
    <div>
      <label>
        Bill Amount:
        <input
          type="number"
          value={billAmount}
          onChange={(e) => setBillAmount(e.target.value)}
        />
      </label>
      <br />

      <label>
        Tip Percentage:
        <select
          value={tipPercentage !== null ? tipPercentage : "custom"}
          onChange={(e) =>
            setTipPercentage(
              e.target.value === "custom" ? null : parseFloat(e.target.value)
            )
          }
        >
          <option value="5">5%</option>
          <option value="10">10%</option>
          <option value="15">15%</option>
          <option value="25">25%</option>
          <option value="50">50%</option>
          <option value="custom">Custom</option>
        </select>
      </label>
      {tipPercentage === null && (
        <input
          type="number"
          placeholder="Custom Tip Amount"
          value={customTipAmount}
          onChange={(e) => setCustomTipAmount(e.target.value)}
        />
      )}
      <br />

      <label>
        Number of People:
        <input
          type="number"
          value={numberOfPeople}
          onChange={(e) => setNumberOfPeople(e.target.value)}
        />
      </label>
      <br />

      <button onClick={handleCalculateTip}>Calculate Tip</button>
      <br />

      <div>
        <p>Tip Amount per Person: ${tipAmountPerPerson.toFixed(2)}</p>
        <p>Total Amount per Person: ${totalAmountPerPerson.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default App;
