import React, { useState } from "react";
import { UserOutlined } from "@ant-design/icons";
import { useForm, Controller } from "react-hook-form";
import { Input } from "antd";
import {
  MainContainer,
  FirstConatiner,
  SecContainer,
  HeadingSection,
  TextSection,
  RightConatiner,
  LeftContainer,
  FirstRightConatiner,
  InputSection,
  MidSection,
  TipSection,
  LastSection,
  LastInputSection,
  LeftDarkContainer,
  TopTextDetails,
  MidTextDetails,
  TextInRow,
  BottomText,
  FirstText,
  SecTextForDetails,
  ButonContainer,
  BottomTextSection,
  SPlTextSection,
  SelectTipContainer,
  ButtonText,
} from "../../styles/views/splitter";

export const SplitterView = () => {
  const [tip, setTip] = useState([
    { percentage: "5%", isCustom: false, isClick: false },
    { percentage: "10%", isCustom: false, isClick: false },
    { percentage: "15%", isCustom: false, isClick: false },
    { percentage: "25%", isCustom: false, isClick: false },
    { percentage: "50%", isCustom: false, isClick: false },
    { percentage: "Custom", isCustom: false, isClick: false },
  ]);

  const { control, handleSubmit, setValue } = useForm({
    defaultValues: {
      bill: "",
      tip: "",
      numberOfPeople: "",
    },
  });

  const handleTipClick = (clickedPercentage) => {
    setValue("tip", clickedPercentage);
    handleSubmit((data) => console.log(data))();
    const updatedTip = tip.map((item) => ({
      ...item,
      isClick: item.percentage === clickedPercentage,
      isCustom:
        item.percentage === "Custom" && item.percentage === clickedPercentage,
    }));

    setTip(updatedTip);
    handleSubmit(onSubmit)();
  };

  const onChangeBill = () => {
    handleSubmit(onSubmit)();
  };

  const onChangeCustomTip = () => {
    handleSubmit(onSubmit)();
  };

  const onChangeNumberOfPeople = () => {
    handleSubmit(onSubmit)();
  };

  const [billAmount, setBillAmount] = useState(0);
  const [tipPercentage, setTipPercentage] = useState(15);
  const [numberOfPeople, setNumberOfPeople] = useState(1);

  const calculateTip = () => {
    const tipAmount = (billAmount * tipPercentage) / 100;
    const totalAmount = billAmount + tipAmount;
    const tipPerPerson = tipAmount / numberOfPeople;
    const totalPerPerson = totalAmount / numberOfPeople;

    return {
      tipPerPerson,
      totalPerPerson,
    };
  };

  const onSubmit = (data) => {
    console.log("data", data);
    setBillAmount(parseFloat(data.bill) || 0);
    setTipPercentage(parseFloat(data.tip) || 0);
    setNumberOfPeople(parseInt(data.numberOfPeople, 10) || 1);
  };

  // bill: "s";
  // customTip: "s";
  // numberOfPeople: "s";
  // tip: "Custom";

  return (
    <form>
      <MainContainer>
        <div>
          <h2>Results:</h2>
          <p>Tip per person: ${calculateTip().tipPerPerson.toFixed(2)}</p>
          <p>Total per person: ${calculateTip().totalPerPerson.toFixed(2)}</p>
        </div>
        <FirstConatiner>
          <HeadingSection>
            <SPlTextSection>SPLT </SPlTextSection>
            <BottomTextSection>TTER</BottomTextSection>
          </HeadingSection>
        </FirstConatiner>
        <SecContainer>
          <RightConatiner>
            <FirstRightConatiner>
              <TextSection>Bill</TextSection>

              <InputSection>
                <Input
                  prefix={"$"}
                  type="number"
                  placeholder="0"
                  onChange={(e) => setBillAmount(e.target.value)}
                />
              </InputSection>
            </FirstRightConatiner>
            <MidSection>
              <TextSection>Select Tip %</TextSection>

              <TipSection>
                {tip.map((item) => (
                  <div key={item.key}>
                    <SelectTipContainer
                      onClick={() => handleTipClick(item.percentage)}
                      isClick={item.isClick}
                      isCustomBackground={item.isCustom}
                    >
                      {item.isCustom === true ? (
                        <input
                          type="number"
                          placeholder="Custom Tip Amount"
                          value={customTipAmount}
                          onChange={(e) => setCustomTipAmount(e.target.value)}
                        />
                      ) : (
                        item.percentage
                      )}
                    </SelectTipContainer>
                  </div>
                ))}
              </TipSection>
            </MidSection>
            <LastSection>
              <TextSection>Number of People</TextSection>

              <LastInputSection>

                    <Input
                      {...field}
                      placeholder="0"
                      onChange={(e) => {
                        field.onChange(e);
                        onChangeNumberOfPeople();
                      }}
                      prefix={<UserOutlined className="site-form-item-icon" />}
                    />
                  )}
                />
              </LastInputSection>
            </LastSection>
          </RightConatiner>
          <LeftContainer>
            <LeftDarkContainer>
              <TopTextDetails>
                <TextInRow>
                  <FirstText>Tip Amount</FirstText>
                  <SecTextForDetails>/Person</SecTextForDetails>
                </TextInRow>
                <BottomText>$0.0</BottomText>
              </TopTextDetails>
              <MidTextDetails>
                <TextInRow>
                  <FirstText>Total</FirstText>
                  <SecTextForDetails>/Person</SecTextForDetails>
                </TextInRow>
                <BottomText>$0.0</BottomText>
              </MidTextDetails>
              <ButonContainer>
                <ButtonText>Reset</ButtonText>
              </ButonContainer>
            </LeftDarkContainer>
          </LeftContainer>
        </SecContainer>
      </MainContainer>
    </form>
  );
};
