import React, { useState, useEffect } from "react";
import { UserOutlined } from "@ant-design/icons";
import { tipcalCalculateData } from "../../";
import {
  MainContainer,
  FirstConatiner,
  SecContainer,
  HeadingSection,
  TextSection,
  RightConatiner,
  LeftContainer,
  FirstRightConatiner,
  InputSection,
  MidSection,
  TipSection,
  LastSection,
  LastInputSection,
  LeftDarkContainer,
  TopTextDetails,
  MidTextDetails,
  TextInRow,
  BottomText,
  FirstText,
  SecTextForDetails,
  ButonContainer,
  BottomTextSection,
  SPlTextSection,
  SelectTipContainer,
  ButtonText,
  StyledNumberInput,
} from "../../styles/views/splitter";

export const SplitterView = () => {
  const [billAmount, setBillAmount] = useState("");
  const [tipPercentage, setTipPercentage] = useState(null);
  const [customTipAmount, setCustomTipAmount] = useState("");
  const [numberOfPeople, setNumberOfPeople] = useState("");
  const [tipAmountPerPerson, setTipAmountPerPerson] = useState(0);
  const [totalAmountPerPerson, setTotalAmountPerPerson] = useState(0);
  const [tip, setTip] = useState(tipcalCalculateData);

  const handleTipClick = (clickedPercentage) => {
    setTipPercentage(parseFloat(clickedPercentage));
    const updatedTip = tip.map((item) => ({
      ...item,
      isClick: item.percentage === clickedPercentage,
      isCustom:
        item.percentage === "Custom" && item.percentage === clickedPercentage,
    }));

    setTip(updatedTip);
  };
  console.log("tipPercentage", tipPercentage);
  console.log("customTipAmount", customTipAmount);

  const handleCalculateTip = () => {
    let tipAmount = 0;

    const selectedTip = tip.find((item) => item.isClick);

    if (selectedTip) {
      if (selectedTip.isCustom && customTipAmount !== "") {
        tipAmount = parseFloat(customTipAmount);
      } else {
        tipAmount = (billAmount * parseFloat(selectedTip.percentage)) / 100;
      }
    }

    const totalAmount = parseFloat(billAmount) + tipAmount;
    const numberOfPeopleFloat = parseFloat(numberOfPeople);

    setTipAmountPerPerson(tipAmount / numberOfPeopleFloat);
    setTotalAmountPerPerson(totalAmount / numberOfPeopleFloat);
  };

  useEffect(() => {
    handleCalculateTip();
  }, [billAmount, tipPercentage, customTipAmount, numberOfPeople]);

  return (
    <MainContainer>
      <FirstConatiner>
        <HeadingSection>
          <SPlTextSection>SPLT </SPlTextSection>
          <BottomTextSection>TTER</BottomTextSection>
        </HeadingSection>
      </FirstConatiner>
      <SecContainer>
        <RightConatiner>
          <FirstRightConatiner>
            <TextSection>Bill</TextSection>

            <InputSection>
              <StyledNumberInput
                prefix={"$"}
                type="number"
                placeholder="0"
                onChange={(e) => setBillAmount(e.target.value)}
              />
            </InputSection>
          </FirstRightConatiner>
          <MidSection>
            <TextSection>Select Tip %</TextSection>

            <TipSection>
              {tip.map((item) => (
                <div key={item.key}>
                  <SelectTipContainer
                    onClick={() => handleTipClick(item.percentage)}
                    isClick={item.isClick}
                    isCustomBackground={item.isCustom}
                  >
                    {item.isCustom === true ? (
                      <StyledNumberInput
                        type="number"
                        placeholder="Custom Tip Amount"
                        value={customTipAmount}
                        onChange={(e) => setCustomTipAmount(e.target.value)}
                        prefix={"$"}
                      />
                    ) : (
                      item.percentage
                    )}
                  </SelectTipContainer>
                </div>
              ))}
            </TipSection>
          </MidSection>
          <LastSection>
            <TextSection>Number of People</TextSection>

            <LastInputSection>
              <StyledNumberInput
                placeholder="0"
                type="number"
                value={numberOfPeople}
                onChange={(e) => setNumberOfPeople(e.target.value)}
                prefix={<UserOutlined className="site-form-item-icon" />}
              />
            </LastInputSection>
          </LastSection>
        </RightConatiner>
        <LeftContainer>
          <LeftDarkContainer>
            <TopTextDetails>
              <TextInRow>
                <FirstText>Tip Amount</FirstText>
                <SecTextForDetails>/Person</SecTextForDetails>
              </TextInRow>
              <BottomText>${tipAmountPerPerson.toFixed(2)}</BottomText>
            </TopTextDetails>
            <MidTextDetails>
              <TextInRow>
                <FirstText>Total</FirstText>
                <SecTextForDetails>/Person</SecTextForDetails>
              </TextInRow>
              <BottomText>${totalAmountPerPerson.toFixed(2)}</BottomText>
            </MidTextDetails>
            <ButonContainer>
              <ButtonText>Reset</ButtonText>
            </ButonContainer>
          </LeftDarkContainer>
        </LeftContainer>
      </SecContainer>
    </MainContainer>
  );
};
