import React, { useState } from "react";
import { UserOutlined } from "@ant-design/icons";
import { useForm, Controller } from "react-hook-form";
import { Input } from "antd";
import {
  MainContainer,
  FirstConatiner,
  SecContainer,
  HeadingSection,
  TextSection,
  RightConatiner,
  LeftContainer,
  FirstRightConatiner,
  InputSection,
  MidSection,
  TipSection,
  LastSection,
  LastInputSection,
  LeftDarkContainer,
  TopTextDetails,
  MidTextDetails,
  TextInRow,
  BottomText,
  FirstText,
  SecTextForDetails,
  ButonContainer,
  BottomTextSection,
  SPlTextSection,
  SelectTipContainer,
  ButtonText,
} from "../../styles/views/splitter";

export const SplitterView = () => {
  const [tip, setTip] = useState([
    { percentage: "5%", isCustom: false, isClick: false },
    { percentage: "10%", isCustom: false, isClick: false },
    { percentage: "15%", isCustom: false, isClick: false },
    { percentage: "25%", isCustom: false, isClick: false },
    { percentage: "50%", isCustom: false, isClick: false },
    { percentage: "Custom", isCustom: false, isClick: false },
  ]);

  const handleTipClick = (clickedPercentage) => {
    const updatedTip = tip.map((item) => ({
      ...item,
      isClick: item.percentage === clickedPercentage,
      isCustom:
        item.percentage === "Custom" && item.percentage === clickedPercentage,
    }));

    setTip(updatedTip);
  };

  const { control, handleSubmit } = useForm({
    defaultValues: {
      bill: "",
      tip: "5%",
      numberOfPeople: "",
    },
  });

  const onSubmit = (data) => {
    // Handle form submission logic here
    console.log(data);
  };
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <MainContainer>
        <FirstConatiner>
          <HeadingSection>
            <SPlTextSection>SPLT </SPlTextSection>
            <BottomTextSection>TTER</BottomTextSection>
          </HeadingSection>
        </FirstConatiner>
        <SecContainer>
          <RightConatiner>
            <FirstRightConatiner>
              <TextSection>Bill</TextSection>
              <InputSection>
                <Controller
                  name="bill"
                  control={control}
                  render={({ field }) => (
                    <Input
                      {...field}
                      placeholder="$"
                      onChange={(e) => {
                        field.onChange(e);
                        setValue("bill", e.target.value);
                      }}
                    />
                  )}
                />
              </InputSection>
            </FirstRightConatiner>
            <MidSection>
              <TextSection>Select Tip N</TextSection>
              <TipSection>
                {tip.map((item) => (
                  <div key={item.key}>
                    <SelectTipContainer
                      onClick={() => handleTipClick(item.percentage)}
                      isClick={item.isClick}
                      isCustomBackground={item.isCustom}
                    >
                      {item.isCustom === true ? (
                        <Input placeholder="0" />
                      ) : (
                        item.percentage
                      )}
                    </SelectTipContainer>
                  </div>
                ))}
              </TipSection>
            </MidSection>
            <LastSection>
              <TextSection>Number of People</TextSection>

              <LastInputSection>
                <Input
                  placeholder="Enter your username"
                  prefix={<UserOutlined className="site-form-item-icon" />}
                />
              </LastInputSection>
            </LastSection>
          </RightConatiner>
          <LeftContainer>
            <LeftDarkContainer>
              <TopTextDetails>
                <TextInRow>
                  <FirstText>Tip Amount</FirstText>
                  <SecTextForDetails>/Person</SecTextForDetails>
                </TextInRow>
                <BottomText>$0.0</BottomText>
              </TopTextDetails>
              <MidTextDetails>
                <TextInRow>
                  <FirstText>Total</FirstText>
                  <SecTextForDetails>/Person</SecTextForDetails>
                </TextInRow>
                <BottomText>$0.0</BottomText>
              </MidTextDetails>
              <ButonContainer>
                <ButtonText>Reset</ButtonText>
              </ButonContainer>
            </LeftDarkContainer>
          </LeftContainer>
        </SecContainer>
      </MainContainer>
    </form>
  );
};
