import React, { useState } from "react";

const TipCalculator = () => {
  const [billAmount, setBillAmount] = useState(0);
  const [tipPercentage, setTipPercentage] = useState(15);
  const [numberOfPeople, setNumberOfPeople] = useState(1);

  const calculateTip = () => {
    const tipAmount = (billAmount * tipPercentage) / 100;
    const totalAmount = billAmount + tipAmount;
    const tipPerPerson = tipAmount / numberOfPeople;
    const totalPerPerson = totalAmount / numberOfPeople;

    return {
      tipPerPerson,
      totalPerPerson,
    };
  };

  const handleBillChange = (event) => {
    setBillAmount(parseFloat(event.target.value) || 0);
  };

  const handleTipChange = (event) => {
    setTipPercentage(parseFloat(event.target.value) || 0);
  };

  const handlePeopleChange = (event) => {
    setNumberOfPeople(parseInt(event.target.value, 10) || 1);
  };

  return (
    <div>
      <label>
        Bill Amount: $
        <input type="number" value={billAmount} onChange={handleBillChange} />
      </label>
      <br />

      <label>
        Tip Percentage:
        <select value={tipPercentage} onChange={handleTipChange}>
          <option value={5}>5%</option>
          <option value={10}>10%</option>
          <option value={15}>15%</option>
          <option value={25}>25%</option>
          <option value={50}>50%</option>
          <option value="custom">Custom</option>
        </select>
      </label>
      {tipPercentage === "custom" && (
        <input
          type="number"
          placeholder="Enter custom tip percentage"
          onChange={handleTipChange}
        />
      )}
      <br />

      <label>
        Number of People:
        <input
          type="number"
          value={numberOfPeople}
          onChange={handlePeopleChange}
        />
      </label>
      <br />

      <div>
        <h2>Results:</h2>
        <p>Tip per person: ${calculateTip().tipPerPerson.toFixed(2)}</p>
        <p>Total per person: ${calculateTip().totalPerPerson.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default TipCalculator;
